import{_ as t,c as a,o as l,aj as n}from"./chunks/framework.e3UxQEvm.js";const u=JSON.parse('{"title":"Payment & Orders","description":"","frontmatter":{},"headers":[],"relativePath":"en/advanced-features/payment.md","filePath":"en/advanced-features/payment.md"}'),r={name:"en/advanced-features/payment.md"};function i(o,e,s,d,c,p){return l(),a("div",null,[...e[0]||(e[0]=[n('<h1 id="payment-orders" tabindex="-1">Payment &amp; Orders <a class="header-anchor" href="#payment-orders" aria-label="Permalink to “Payment &amp; Orders”">​</a></h1><h2 id="payment-methods" tabindex="-1">Payment Methods <a class="header-anchor" href="#payment-methods" aria-label="Permalink to “Payment Methods”">​</a></h2><p>The bot supports multiple payment methods:</p><ul><li>Alipay</li><li>WeChat Pay</li><li>Other third-party payment platforms</li></ul><h2 id="payment-process" tabindex="-1">Payment Process <a class="header-anchor" href="#payment-process" aria-label="Permalink to “Payment Process”">​</a></h2><ol><li><p><strong>Select Plan and Period</strong></p><ul><li>Select plan from the plan list</li><li>Choose payment period</li></ul></li><li><p><strong>Select Payment Method</strong></p><ul><li>System displays all available payment methods</li><li>Different payment methods may charge different handling fees</li><li>Choose your preferred payment method</li></ul></li><li><p><strong>Complete Payment</strong></p><ul><li>Click payment button to jump to payment page</li><li>Or scan QR code to complete payment</li><li>Return to bot after payment</li></ul></li><li><p><strong>Confirm Order</strong></p><ul><li>Click &quot;Check Order&quot; button</li><li>System automatically detects payment status</li><li>Service is automatically activated after successful payment</li></ul></li></ol><h2 id="order-management" tabindex="-1">Order Management <a class="header-anchor" href="#order-management" aria-label="Permalink to “Order Management”">​</a></h2><p><strong>View Order Status</strong></p><ul><li>Click &quot;Check Order&quot; on payment page</li><li>Or use <code>/pay check order_number</code> command</li></ul><p><strong>Order Status Description</strong></p><ul><li><strong>Pending Payment</strong> - Order created, waiting for payment</li><li><strong>Paid</strong> - Payment successful, waiting for activation</li><li><strong>Completed</strong> - Order completed, service activated</li><li><strong>Cancelled</strong> - Order cancelled</li></ul><p><strong>Cancel Order</strong></p><ul><li>Click &quot;Cancel Order&quot; on payment page</li><li>Or use <code>/pay cancel order_number</code> command</li><li>Only pending payment orders can be cancelled</li></ul><h2 id="payment-notes" tabindex="-1">Payment Notes <a class="header-anchor" href="#payment-notes" aria-label="Permalink to “Payment Notes”">​</a></h2><ol><li><p><strong>After Payment</strong></p><ul><li>If the order is not automatically activated, please click the &quot;Check Order&quot; button</li><li>Do not place duplicate orders to avoid duplicate charges</li></ul></li><li><p><strong>Payment Recipient Note</strong></p><ul><li>Due to the special nature of the service, the payment recipient may show as an individual or small store</li><li>This is normal, please feel free to pay</li></ul></li><li><p><strong>Payment Failure</strong></p><ul><li>If payment fails, you can reselect payment method</li><li>Or contact customer service</li></ul></li><li><p><strong>Balance Deduction</strong></p><ul><li>Account balance can be used to deduct plan fees</li><li>Balance comes from check-in rewards and invite rewards</li><li>Only need to pay the difference after deduction</li></ul></li></ol>',15)])])}const y=t(r,[["render",i]]);export{u as __pageData,y as default};
