import{_ as i,c as a,o as e,aj as n}from"./chunks/framework.e3UxQEvm.js";const g=JSON.parse('{"title":"套餐购买 - OneTapLink Bot 使用指南","description":"了解如何通过 OneTapLink Bot 购买 VPN/机场服务套餐，支持多种支付方式和付费周期","frontmatter":{"title":"套餐购买 - OneTapLink Bot 使用指南","description":"了解如何通过 OneTapLink Bot 购买 VPN/机场服务套餐，支持多种支付方式和付费周期","keywords":"套餐购买, VPN, 机场, 订阅, 支付, OneTapLink"},"headers":[],"relativePath":"zh/核心功能/套餐购买.md","filePath":"zh/核心功能/套餐购买.md"}'),r={name:"zh/核心功能/套餐购买.md"};function s(t,l,o,d,p,c){return e(),a("div",null,[...l[0]||(l[0]=[n('<h1 id="套餐购买" tabindex="-1">套餐购买 <a class="header-anchor" href="#套餐购买" aria-label="Permalink to “套餐购买”">​</a></h1><h2 id="功能说明" tabindex="-1">功能说明 <a class="header-anchor" href="#功能说明" aria-label="Permalink to “功能说明”">​</a></h2><p>通过机器人可以方便地浏览和购买 VPN/机场服务套餐，支持多种支付方式。</p><h2 id="使用方法" tabindex="-1">使用方法 <a class="header-anchor" href="#使用方法" aria-label="Permalink to “使用方法”">​</a></h2><p><strong>方式一：命令调用</strong></p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>/plans</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><strong>方式二：关键词触发</strong> 在群组或私聊中发送以下关键词：</p><ul><li><code>机场</code></li><li><code>VPN</code></li><li><code>套餐</code></li><li><code>购买</code></li><li><code>订阅</code></li></ul><p><strong>方式三：快捷按钮</strong></p><ul><li>点击底部键盘的「🌍️ 机场&amp;VPN」按钮</li><li>或点击消息中的内联按钮</li></ul><h2 id="购买流程" tabindex="-1">购买流程 <a class="header-anchor" href="#购买流程" aria-label="Permalink to “购买流程”">​</a></h2><ol><li><p><strong>查看套餐列表</strong></p><ul><li>发送 <code>/plans</code> 或点击相应按钮</li><li>系统会显示所有可用套餐</li><li>每个套餐显示名称和最低价格</li></ul></li><li><p><strong>选择套餐</strong></p><ul><li>点击想要购买的套餐</li><li>查看套餐详细说明（如适用）</li><li>选择付费周期（周付/月付/季付/半年付/年付等）</li></ul></li><li><p><strong>选择支付方式</strong></p><ul><li>系统显示所有可用支付方式</li><li>不同支付方式可能收取不同手续费</li><li>选择您偏好的支付方式</li></ul></li><li><p><strong>完成支付</strong></p><ul><li>点击支付按钮跳转到支付页面</li><li>或扫描二维码完成支付</li><li>支付完成后点击「检测订单」确认状态</li></ul></li><li><p><strong>获取订阅链接</strong></p><ul><li>支付成功后，发送 <code>/plans</code> 获取订阅链接</li><li>复制订阅链接导入到客户端使用</li><li>支持 Shadowrocket、V2rayN 等客户端</li></ul></li></ol><h2 id="套餐类型" tabindex="-1">套餐类型 <a class="header-anchor" href="#套餐类型" aria-label="Permalink to “套餐类型”">​</a></h2><ul><li><strong>周付套餐</strong> - 按周付费，适合短期使用</li><li><strong>月付套餐</strong> - 按月付费，性价比高</li><li><strong>季付套餐</strong> - 按季度付费，享受折扣</li><li><strong>半年付套餐</strong> - 按半年付费，更优惠</li><li><strong>年付套餐</strong> - 按年付费，最优惠</li><li><strong>流量包</strong> - 单独购买流量，不包含时长</li></ul><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to “注意事项”">​</a></h2><ul><li>长周期套餐通常享受更多折扣</li><li>支付完成后如未自动开通，请点击「检测订单」按钮</li><li>支持余额抵扣，签到和邀请获得的余额可直接使用</li><li>部分套餐可能限制权限组，请确认您的权限</li></ul>',16)])])}const u=i(r,[["render",s]]);export{g as __pageData,u as default};
