import{_ as i,c as a,o as r,aj as t}from"./chunks/framework.e3UxQEvm.js";const d=JSON.parse('{"title":"支付与订单","description":"","frontmatter":{},"headers":[],"relativePath":"zh/高级功能/支付与订单.md","filePath":"zh/高级功能/支付与订单.md"}'),o={name:"zh/高级功能/支付与订单.md"};function e(n,l,s,p,c,g){return r(),a("div",null,[...l[0]||(l[0]=[t('<h1 id="支付与订单" tabindex="-1">支付与订单 <a class="header-anchor" href="#支付与订单" aria-label="Permalink to “支付与订单”">​</a></h1><h2 id="支付方式" tabindex="-1">支付方式 <a class="header-anchor" href="#支付方式" aria-label="Permalink to “支付方式”">​</a></h2><p>机器人支持多种支付方式：</p><ul><li>支付宝</li><li>微信支付</li><li>其他第三方支付平台</li></ul><h2 id="支付流程" tabindex="-1">支付流程 <a class="header-anchor" href="#支付流程" aria-label="Permalink to “支付流程”">​</a></h2><ol><li><p><strong>选择套餐和周期</strong></p><ul><li>在套餐列表中选择套餐</li><li>选择付费周期</li></ul></li><li><p><strong>选择支付方式</strong></p><ul><li>系统显示所有可用支付方式</li><li>不同支付方式可能收取不同手续费</li><li>选择您偏好的支付方式</li></ul></li><li><p><strong>完成支付</strong></p><ul><li>点击支付按钮跳转到支付页面</li><li>或扫描二维码完成支付</li><li>支付完成后返回机器人</li></ul></li><li><p><strong>确认订单</strong></p><ul><li>点击「检测订单」按钮</li><li>系统自动检测支付状态</li><li>支付成功后自动开通服务</li></ul></li></ol><h2 id="订单管理" tabindex="-1">订单管理 <a class="header-anchor" href="#订单管理" aria-label="Permalink to “订单管理”">​</a></h2><p><strong>查看订单状态</strong></p><ul><li>在支付页面点击「检测订单」</li><li>或使用 <code>/pay check 订单号</code> 命令</li></ul><p><strong>订单状态说明</strong></p><ul><li><strong>待支付</strong> - 订单已创建，等待支付</li><li><strong>已支付</strong> - 支付成功，等待开通</li><li><strong>已完成</strong> - 订单已完成，服务已开通</li><li><strong>已取消</strong> - 订单已取消</li></ul><p><strong>取消订单</strong></p><ul><li>在支付页面点击「取消订单」</li><li>或使用 <code>/pay cancel 订单号</code> 命令</li><li>仅待支付订单可取消</li></ul><h2 id="支付注意事项" tabindex="-1">支付注意事项 <a class="header-anchor" href="#支付注意事项" aria-label="Permalink to “支付注意事项”">​</a></h2><ol><li><p><strong>支付完成后</strong></p><ul><li>如果订单未自动开通，请点击「检测订单」按钮</li><li>不要重复下单，避免重复扣款</li></ul></li><li><p><strong>收款方说明</strong></p><ul><li>由于服务特殊性，收款方可能显示为个人或小卖部</li><li>这是正常现象，请放心支付</li></ul></li><li><p><strong>支付失败</strong></p><ul><li>如支付失败，可重新选择支付方式</li><li>或联系客服处理</li></ul></li><li><p><strong>余额抵扣</strong></p><ul><li>账户余额可用于抵扣套餐费用</li><li>余额来自签到奖励和邀请奖励</li><li>抵扣后只需支付差额部分</li></ul></li></ol>',15)])])}const u=i(o,[["render",e]]);export{d as __pageData,u as default};
